import arbiter from '../../arbiter/arbiter';
import { useAppContext }from '../../contexts/Context'
import { generateCandidates } from '../../reducer/actions/move';

const Piece = ({rank, file, piece}) => {
    const onDragStart = e => {
        e.dataTransfer.setData("text/plain", JSON.stringify({rank, file, piece}));
        const {state, dispatch} = useAppContext();
        const candidates = generateCandidates(state.position, {rank, file}, arbiter);
        dispatch({type: 'SET_CANDIDATES', payload: candidates});
    }
    return (
        <div 
            className={`piece ${piece} p-${file}${rank}`}
            draggable={true}   
            onDragStart={onDragStart} 
            onDragEnd={onDragEnd}

        />)
}
export default Piece;
