import './Pieces.css';
import Piece from './Piece';
import { useState } from 'react';

const Pieces = () => {
    const position = new Array(8).fill().map((x, i) => new Array(8).fill(null));
    for (let i = 0; i < 8; i++) {
        position[1][i] = "wp"
        position[6][i] = "bp"
    }
    position[0][0] = 'wr';
    position[0][1] = 'wn';
    position[0][2] = 'wb';
    position[0][3] = 'wq';
    position[0][4] = 'wk';
    position[0][5] = 'wb';
    position[0][6] = 'wn';
    position[0][7] = 'wr';

    position[7][0] = 'br';
    position[7][1] = 'bn';
    position[7][2] = 'bb';
    position[7][3] = 'bq';
    position[7][4] = 'bk';
    position[7][5] = 'bb';
    position[7][6] = 'bn';

    console.log(position);

    return <div className="pieces">
        {position.map((r, rank) =>
            rank.map((f, file) =>
                position[rank][file] 
                 ? <Piece 
                    key={rank + '-' +file}
                    rank={rank}
                    file={file} 
                    piece={position[rank][file]}
                    /> : null
            )
        )}
    </div>;
    
}
export default Pieces;
